INSERT INTO `anime` VALUES (15993, 'Ginga e Kickoff!!: Natsuyasumi Special', 'https://cdn.myanimelist.net/images/anime/9/43145.jpg', 'Special', '-', 'Clip show with soccer commentators providing commentary. \n', '2012', 'Summer');
