INSERT INTO `anime` VALUES (19457, 'Green Saver', 'https://cdn.myanimelist.net/images/anime/12/52163.jpg', 'TV', 'Original', 'Nalong, Ukkya, and Longman have teamed up to defend the Earth from alien invaders who are here to pollute the environment! Lulu forest is no longer the main threatened area as the trio travels the world and helps out other animals in need. ', '2010', 'Winter');
