INSERT INTO `anime` VALUES (20221, 'Minami-ke Natsuyasumi', 'https://cdn.myanimelist.net/images/anime/5/64505.jpg', 'OVA', 'Manga', 'Second Minami-ke OVA bundled with the 11th volume of the manga.\n\nChiaki, Kana, and Haruka decide how to spend their last few days of summer together.  Kana struggles to finish her homework.', '2013', 'Summer');
INSERT INTO `anime` VALUES (20261, 'W-Tails Cat: A Strange Presence', 'https://cdn.myanimelist.net/images/anime/4/53957.jpg', 'ONA', 'Other', 'A hentai based on Shirow Masamune\'s W-TAILS CAT art books.', '2013', 'Spring');
