INSERT INTO `anime` VALUES (23367, 'Kitakubu Katsudou Kiroku: Miniature Theater', 'https://cdn.myanimelist.net/images/anime/5/60367.jpg', 'Special', 'Manga', 'Unaired \"Miniature Theater\" (the specials inserted at the end of the main episodes) included in the Kitakubu Katsudou Kiroku DVD/BD volumes 2, 3 and 4.\n', '2013', 'Fall');
