INSERT INTO `anime` VALUES (23721, 'Carino Coni', 'https://cdn.myanimelist.net/images/anime/8/61725.jpg', 'TV', '-', 'A shy little Carino Coni is a rabbit boy who just started to live alone in the forest away from his family.\r\nEveryday Coni\'s heart raises with joy playing with his siblings who lives nearby, but suddenly cries of lonesomeness, which he cannot explain.\r\n\r\nThis story is about all heart warming stories Coni encounters, and how he himself become not so lonely anymore as he gradually grows up.\r\n\r\n(Source: playsetproducts.jp)', '2014', 'Spring');
INSERT INTO `anime` VALUES (23725, 'Strike Witches: Operation Victory Arrow', 'https://cdn.myanimelist.net/images/anime/12/67859.jpg', 'OVA', 'Original', 'Operation Victory Arrow is set in the period between the end of Operation Mars in the second television series and the 2012 Strike Witches movie. The three 30-minute short stories depict the lives of the members who returned to their respective homes.\r\n\r\n(Source: ANN)', '2014', 'Fall');
