INSERT INTO `anime` VALUES (23835, 'Moonrakers', 'https://cdn.myanimelist.net/images/anime/11/61635.jpg', 'ONA', 'Manga', 'An accident leaves Taishi stranded on the lunar surface, unable to return to Earth. A girl helps him in his despair, but he is oblivious to her true identity.\n\n(Source: ANN)', '2014', 'Spring');
