INSERT INTO `anime` VALUES (25619, 'Genki Genki Non-tan: Spoon Tan Tan Tan', 'https://cdn.myanimelist.net/images/anime/6/65141.jpg', 'OVA', '-', 'This edition focuses on teaching kids good house manners. Such as eating all the food on your plate; to taking naps when instructed to so you can grow up big and strong; to avoiding wetting the bed and how to handle the situation if you do.', '2013', 'Spring');
