INSERT INTO `anime` VALUES (29395, 'Fafa Movie', 'https://cdn.myanimelist.net/images/anime/10/70665.jpg', 'ONA', 'Other', 'A promotional anime for Nissan Soap\'s Fafa laundry mascot. These two ONA films were promoting the AiR Fafa project. The project itself was a type of augmented reality app that people could play with.\r\n\r\nNorth American viewers may recognize Fafa under a different name \"Snuggle\". The mascot for the fabric softener brand owned by Sun Products.', '2010', 'Winter');
INSERT INTO `anime` VALUES (29397, 'Pinky Monkey x FaFa Collaboration Animation: At Afternoon of Sunny Day', 'https://cdn.myanimelist.net/images/anime/4/70667.jpg', 'ONA', 'Other', 'A collaboration anime between Frente International\'s Pinky candy mascot and Nissan Soap\'s Fafa laundry mascot.\r\n\r\nNorth American viewers may recognize Fafa under a different name \"Snuggle\". The mascot for the fabric softener brand owned by Sun Products.', '2011', 'Winter');
INSERT INTO `anime` VALUES (29399, 'Ousama Monogatari', 'https://cdn.myanimelist.net/images/anime/10/70669.jpg', 'TV', 'Original', 'Stop-motion animation made out of fabric following a curious king sneaking out of his castle.', '2010', 'Spring');
INSERT INTO `anime` VALUES (29413, 'Dog Days Recap', 'https://cdn.myanimelist.net/images/anime/5/70713.jpg', 'Special', '-', 'Recap of the first two seasons of Dog Days series narrated by Mikako Takahashi. Aired simultaneously with the first episode of the third season.', '2015', 'Winter');
