INSERT INTO `anime` VALUES (29730, 'Ienai Koto The Animation', 'https://cdn.myanimelist.net/images/anime/1074/104304.jpg', 'OVA', 'Manga', 'Hayakawa Rena as school advisor caught her sister, Hayakawa Mina having sex with guy teacher. But Rena is blackmailed into being a plaything for men.\r\n\r\n(Source: AniDB)', '2015', 'Winter');
