INSERT INTO `anime` VALUES (31530, 'Classroom☆Crisis: Tabi no Haji wa Uwanuri', 'https://cdn.myanimelist.net/images/anime/11/75999.jpg', 'Special', 'Original', 'Unaired episode included with the third Blu-ray and DVD volume. ', '2015', 'Fall');
