INSERT INTO `anime` VALUES (32409, 'Shinmai Maou no Testament Burst Specials', 'https://cdn.myanimelist.net/images/anime/10/79993.jpg', 'Special', 'Light novel', 'Specials included on the Blu-ray/DVD volumes. Super deformed versions of Mio and Maria narrate Basara\'s various erotic encounters.', '2015', 'Fall');
