INSERT INTO `anime` VALUES (33720, 'Girls & Panzer Movie: 3-pun Chotto de Wakaru!! Girls & Panzer', 'https://cdn.myanimelist.net/images/anime/13/81095.jpg', 'Movie', 'Original', 'A 3-minute intro screened in theaters at the start of the movie. It recaps the TV series to give viewers some context before watching the film.\r\n\r\n(Source: AniDB)', '2015', 'Fall');
INSERT INTO `anime` VALUES (33723, 'Mahou Shoujo? Naria☆Girls Nama de Anime wo Tsukuru Sama', 'https://cdn.myanimelist.net/images/anime/8/81105.jpg', 'ONA', 'Original', 'Urara, Hanabi, and Inaho are three girls who aim to become nationally-renowned characters by creating animation of themselves! They chose the magical girl theme to catch the attention of everyone from children to adults! Can they safely become popular characters in a one-cours magical girl anime?\r\n\r\n(Source: MAL News)', '2016', 'Summer');
