INSERT INTO `anime` VALUES (33726, 'Saiyuuki Reload Blast', 'https://cdn.myanimelist.net/images/anime/11/86675.jpg', 'TV', 'Manga', 'At last, the Sanzou Party has arrived in India. In this foreign land, where the anomaly\'s influence runs rampant, their battles only heighten in their violence.\r\n\r\nAnd also awaiting them is their tragic fate from 500 years ago—\r\n\r\nWhat will they find at the end of their long and treacherous journey?', '2017', 'Summer');
