INSERT INTO `anime` VALUES (33743, 'Fuuka', 'https://cdn.myanimelist.net/images/anime/8/83735.jpg', 'TV', 'Manga', 'Yuu Haruna just moved into town and loves to use Twitter. Out on his way to buy dinner, he bumps into a mysterious girl, Fuuka Akitsuki, who breaks his phone thinking he was trying to take a picture of her panties. How will his new life change now?\r\n\r\n(Source: MAL News)', '2017', 'Winter');
INSERT INTO `anime` VALUES (33744, 'Yahabe', 'https://cdn.myanimelist.net/images/anime/4/81153.jpg', 'OVA', 'Manga', 'Yosuke Kamogawa had nothing to live for, but a mysterious pyramid appears called Yahabe! With the power to turn back time, how will Yosuke\'s life change?\r\n\r\n(Source: AniDB)', '2012', 'Summer');
INSERT INTO `anime` VALUES (33746, 'Anitore! XX: Hitotsu Yane no Shita de', 'https://cdn.myanimelist.net/images/anime/10/82252.jpg', 'TV', 'Original', 'Sequel of Anitore! EX. \r\n\r\nNew member Sakura Izumi joins the five idol candidates of the previous season. A second year middle schooler, she often wears a big smile on her face.\r\n\r\n(Source: ANN)', '2016', 'Fall');
