INSERT INTO `anime` VALUES (33936, 'JA Kumamoto Keizairen', 'https://cdn.myanimelist.net/images/anime/10/81646.jpg', 'ONA', 'Original', 'The Kumamoto Prefecture Agricultural Cooperative Union\'s website began streaming an anime commercial titled \'Nikoyaka Shokudou\' (Smiling Restaurant) on Wednesday. The commercial is intended to highlight produce grown in the prefecture, including meat (niku), rice (kome), vegetables (yasai), and fruit (kajitsu). The first characters of each of the words spell \"nikoyaka\" (smiling).', '2016', 'Spring');
INSERT INTO `anime` VALUES (33937, 'Kirai, Demo, Suki', 'https://cdn.myanimelist.net/images/anime/13/81647.jpg', 'ONA', 'Original', 'A commercial to promote HAL Tokyo College of Technology & Design. Composer Teddyloid, rapper DAOKO, director Hibiki Yoshizaki, and character designer PALOW created the commercial. \r\n\r\nThe character is named \"Throne.No.11 HAL mia,\" a girl who wants to change her fate. \r\n\r\n(Source: ANN)', '2016', 'Spring');
