INSERT INTO `anime` VALUES (34757, 'Town Workers', 'https://cdn.myanimelist.net/images/anime/4/83802.jpg', 'ONA', 'Original', 'An omnibus work portraying the lives of youth who have just started to work.\r\n\r\n(Source: Rockwell Eyes)', '2014', 'Summer');
