INSERT INTO `anime` VALUES (34999, 'Kiitarou Shounen no Youkai Enikki: Jonan no Saijitsu', 'https://cdn.myanimelist.net/images/anime/8/84505.jpg', 'Special', 'Web manga', 'An unaired episode 13 of Kiitarou Shounen no Youkai Enikki included on the DVD/BD release. Kiitarou runs into typical ecchi manga tropes due to Lucky Eros albeit he doesn\'t feel \"lucky\" and tries to avoid everything happening between him and the female youkai.', '2017', 'Winter');
