INSERT INTO `anime` VALUES (35004, 'T-sensei', 'https://cdn.myanimelist.net/images/anime/12/87130.jpg', 'ONA', 'Manga', 'The author initially started as a childcare worker living in Tokyo, calling himself T-sensei and describing his life of tending kindergarteners on his Twitter since 2012, which received various positive responses.\r\n\r\n(Source: MAL News)', '2017', 'Spring');
