INSERT INTO `anime` VALUES (35303, 'Furusato Meguri: Nippon no Mukashibanashi', 'https://cdn.myanimelist.net/images/anime/2/85025.jpg', 'TV', 'Other', 'Sequel series to Furusato Saisei: Nihon no Mukashibanashi.', '2018', 'Winter');
INSERT INTO `anime` VALUES (35305, 'Kamiusagi Rope x Tokyo Gas', 'https://cdn.myanimelist.net/images/anime/7/85028.jpg', 'ONA', 'Original', 'A collaboration between Kamiusagi Rope and Tokyo Gas promoting a special gas exhibition campaign.', '2013', 'Summer');
INSERT INTO `anime` VALUES (35306, 'Mori no Kaori x Kamiusagi Rope', 'https://cdn.myanimelist.net/images/anime/12/85029.jpg', 'ONA', 'Original', 'A collaboration between Kamiusagi Rope and Nipponham\'s Mori no Kaori product line. Meant to showcase sausages, bacon, ham, etc. that have a rustic  forest flavor with no added colors and reduced sodium. Episodes were available to view by entering a code online that was given on the inner label of a Mori no Kaori\'s product package.\r\n\r\n', '2013', 'Fall');
INSERT INTO `anime` VALUES (35307, 'Shiseido x Kamiusagi Rope', 'https://cdn.myanimelist.net/images/anime/3/85030.jpg', 'ONA', 'Original', 'A collaboration between Kamiusagi Rope and two of Shiseido\'s product lines: Anessa and Fullmake Washable Base (FWB). These episodes were included on the 6th DVD of Kamiusagi Rope: Warau Asa ni wa Fukuraitaru tte Maji ssuka!? as episode 23 and 24 released on March 18, 2015.', '2014', 'Winter');
