INSERT INTO `anime` VALUES (35434, 'Konbini Kareshi', 'https://cdn.myanimelist.net/images/anime/4/86243.jpg', 'TV', 'Original', 'Six high school students\' lives around the convenience store on their way from school; they talk about their lives and meet girls they end up falling for.\r\n\r\n(Source: MAL News) ', '2017', 'Summer');
INSERT INTO `anime` VALUES (35442, 'Niuniu Elise PV', 'https://cdn.myanimelist.net/images/anime/10/85440.jpg', 'ONA', 'Other', 'The popular character Elise from the program \"WeChat\" is the star of a short animation from DMM Pictures. The Chinese program \"WeChat\" uses stamps similar to Japan\'s LINE service. The titular main character is a baking champ that lives in Sweet Town. \r\n\r\n(Source: ANN)', '2017', 'Winter');
INSERT INTO `anime` VALUES (35443, 'Nui Nui Hinobori San Kyoudai', 'https://cdn.myanimelist.net/images/anime/3/85442.jpg', 'ONA', 'Other', 'The series will center on the three Hinobori brothers, who run a stuffed toy shop called \"Nui Nui\" at the end of Tokyo\'s Kappabashi shopping street. The eldest brother Kaede is the manager, the second son Aoi is in charge of patterns, and the third son Akane is in charge of displays. \r\n\r\n(Source: ANN)', '2017', 'Spring');
INSERT INTO `anime` VALUES (35444, 'Neko to Mata', 'https://cdn.myanimelist.net/images/anime/10/85474.jpg', 'TV', 'Original', 'The series centers around two cats: a younger sister named Neko, and an older brother named Mata. Neko is a nekomata (a mythical two-tailed cat), innocently selfish, and spoiled by her brother. Mata is a neighborhood cat with an eartipped mark as a sign of being neutered. They meet under a streetlight in the city at night to talk.\r\n\r\n(Source: ANN)', '2018', 'Winter');
INSERT INTO `anime` VALUES (35446, 'Residence', 'https://cdn.myanimelist.net/images/anime/9/85444.jpg', 'OVA', 'Manga', 'The series takes place at a special mansion that transforms all who enter into beautiful women. After hearing rumors of a treasure within its walls, a man enters...', '2018', 'Winter');
