INSERT INTO `anime` VALUES (35454, 'Jii Coro Comic', 'https://cdn.myanimelist.net/images/anime/5/85459.jpg', 'ONA', 'Manga', 'A Dangerous Jiisan webisode released on CoroCoro Comic\'s official youtube page for Christmas 2015. In it, Yousuke accidentally breaks Jiisan\'s handheld game and ends up going on a wacky time traveling loop to fix it. ', '2015', 'Fall');
