INSERT INTO `anime` VALUES (36064, 'Fate/Grand Order', 'https://cdn.myanimelist.net/images/anime/13/87181.jpg', 'Special', 'Game', 'Assorted commercials and collaborations for the Fate/Grand Order mobile RPG. These short animations are posted to Type-Moon\'s official YouTube channel and advertise the game itself or promote a new game event (eg. Dead Heat Summer Race! for summer 2017). The animation is unique to these commercials and is not present in the mobile game nor in the TV special of Fate/Grand Order.', '2014', 'Fall');
INSERT INTO `anime` VALUES (36073, 'Aiseki Mogol Girl', 'https://cdn.myanimelist.net/images/anime/5/87194.jpg', 'TV', 'Other', 'The series features colorful stop-motion mascot characters made out of pipe cleaners. In Japanese, \"aiseki\" refers to the situation of sharing a table at a restaurant with a person you don\'t know. The show is surreal datsuryoku-kei comedy with a variety of male guest voice actors who banter with Fuyumi and Haruko at a table in the Mongol Cafe.', '2017', 'Fall');
INSERT INTO `anime` VALUES (36077, 'Macross Δ Movie: Gekijou no Walküre', 'https://cdn.myanimelist.net/images/anime/1217/94351.jpg', 'Movie', 'Original', 'The movie recompiles scenes from the Macross Δ television anime with new scenes to tell a different story.\r\n\r\n(Source: AniDB)', '2018', 'Winter');
