INSERT INTO `anime` VALUES (36108, 'Takunomi.', 'https://cdn.myanimelist.net/images/anime/1869/95731.jpg', 'TV', 'Web manga', '20-year-old Michiru Amatsuki moved to Tokyo after a career change, and now finds herself living in a woman only share house, Stella House Haruno, with a few other women of varying age and occupation. Every night the girls settle down and have a drink or two (or more) as drama ensues.', '2018', 'Winter');
