INSERT INTO `anime` VALUES (36118, 'Onagawa Chuu Baske Bu: 5-nin no Natsu', 'https://cdn.myanimelist.net/images/anime/8/87271.jpg', 'Special', 'Other', 'Based on the true story of the Onagawa Junior High girl\'s basketball team. The team\'s numbers and staff are depleted after the Touhoku earthquake, leaving only five regulars.', '2017', 'Summer');
