INSERT INTO `anime` VALUES (36410, 'Littleforestmello', 'https://cdn.myanimelist.net/images/anime/9/87846.jpg', 'ONA', 'Original', 'An introductory anime of Littleforestfellow, who is a rabbit boy and the second cousin of My Melody. He is nicknamed \"Mello\" (めろぉ). He really likes My Melody and likes to collect buttons with her. His birthday is on January 18th, just like My Melody. \r\n\r\n(Source: Wikia)', '2016', 'Summer');
INSERT INTO `anime` VALUES (36411, 'Mushinin', 'https://cdn.myanimelist.net/images/anime/12/87848.jpg', 'ONA', 'Game', 'Bandai\'s new toy line for boys: Mushinin. It combines bugs (mushi) and ninja (nin). The anime follows the various characters as they train in their ninja arts at a local playground.', '2017', 'Summer');
