INSERT INTO `anime` VALUES (36620, 'Nintama Rantarou: Saraba Ninjutsu Gakuen no Dan', 'https://cdn.myanimelist.net/images/anime/10/88492.jpg', 'Special', '-', 'Rantarou misunderstands an overheard comment from the Headmaster and leaves Ninjutsu Academy in shock. At the same time, the Dokutake Castle ninja team is once again up to no good...', '2017', 'Fall');
