INSERT INTO `anime` VALUES (36671, 'Hayashi-fusai', 'https://cdn.myanimelist.net/images/anime/6/88648.jpg', 'Special', 'Original', 'A video created for Ryou Inoue\'s friends who got married. He showed it during their wedding reception and uploaded it to YouTube two days later.', '2013', 'Spring');
INSERT INTO `anime` VALUES (36673, 'Nakano Harada Kekkon Kyousoukyoku', 'https://cdn.myanimelist.net/images/anime/13/88650.jpg', 'Special', 'Original', 'An introduction video for the bride (Harada Momo) and groom (Nakano-san) to be shown at their wedding reception. The video was uploaded to YouTube a day after the wedding.', '2014', 'Winter');
INSERT INTO `anime` VALUES (36674, 'Shukka Fuufu', 'https://cdn.myanimelist.net/images/anime/4/88651.jpg', 'Special', 'Original', 'Sugawara-san commissioned a video to be shown at her wedding about her and her husband about their love journey.', '2014', 'Fall');
