INSERT INTO `anime` VALUES (36847, 'Nanatsu no Bitoku', 'https://cdn.myanimelist.net/images/anime/9/89129.jpg', 'TV', 'Other', 'Set in the same timeline as Sin: Nanatsu no Taizai, Nanatsu no Bitoku follows a group of angels sent from Heaven to look for a potential \"Savior\" to counter-attack the demonic influence being spread by Lucifer and the other emissaries of Hell.\r\n\r\n(Source: Tokyo Zerostar)', '2018', 'Winter');
INSERT INTO `anime` VALUES (36848, 'Aishen Qiaokeli-ing...II', 'https://cdn.myanimelist.net/images/anime/5/89258.jpg', 'ONA', 'Web manga', 'Jiang Hao Yi, a normal, high school student who is extremely scared of getting into trouble, suddenly becomes the Harem King of his school when a bunch of beautiful girls confess to him! Unfortunately for Jian Hao, he doesn\'t know any of the girls who have confessed to him... What on Earth is happening?', '2017', 'Fall');
