INSERT INTO `anime` VALUES (36858, 'Usuzumizakura: Garo', 'https://cdn.myanimelist.net/images/anime/1428/93934.jpg', 'Movie', 'Original', 'Set during the Heian Period, Kyo is a beautiful city that is proud of its glory. Two beautiful onmyouji meet, and the sad and short-lived beautiful cherry blossom starts to stir after 1000 years.\r\n\r\n(Source: MAL News)', '2018', 'Fall');
INSERT INTO `anime` VALUES (36861, 'Itazura Majo to Nemuranai Machi', 'https://cdn.myanimelist.net/images/anime/3/89169.jpg', 'ONA', 'Original', 'The story is set in New York which is lit up with Christmas lights. Tokyo Metropolitan Police officer Akari Kido is directing traffic while on her international exchange training program, when a witch-like character pulls a \"Christmas Party\" prank. Just before a runaway trailer truck smashes into Akari, a mysterious girl suddenly appears and shouts, \"I\'m going to protect New York and Akari!\"  \r\n\r\n(Source: ANN)', '2017', 'Fall');
