INSERT INTO `anime` VALUES (37530, 'Miyako', 'https://cdn.myanimelist.net/images/anime/1741/92704.jpg', 'ONA', 'Other', 'MONOLITH (a.k.a. Kaoru/Brilliance) provides a poetry reading for Miyako, taken from the perspective of a mentally ill woman stalking another woman.', '2012', 'Summer');
