INSERT INTO `anime` VALUES (37864, 'Yinhun x KFC', 'https://cdn.myanimelist.net/images/anime/1931/92808.jpg', 'ONA', '-', 'Chinese streaming platform Weibo is hosting exclusive Gintama footage. The ad is in Japanese with Chinese subtitles showing the characters enjoying KFC\'s Chinese brand.\r\n\r\nGintama is titled and marketed as Yinhun in China.', '2018', 'Spring');
