INSERT INTO `anime` VALUES (38035, 'Nuresuke JK Ameyadori Rape', 'https://cdn.myanimelist.net/images/anime/1155/103767.jpg', 'ONA', 'Original', 'When a school girl takes shelter from the rain at a bus stop, a middle-aged man comes...\r\nAfter some time of chatting the man, frustrated about the way his family is treating him, suddenly assaults and r*pes her over and over again. \r\n\r\n(Source: DLsite)', '2018', 'Spring');
