INSERT INTO `anime` VALUES (38603, '#Compass', 'https://cdn.myanimelist.net/images/anime/1392/95935.jpg', 'ONA', 'Game', 'NHN PlayArt and Nico Nico\'s #Compass [COMbat Providence AnalysiS System] smartphone game has been inspiring a series shorts in which TMS Entertainment is overseeing the production, but different staff members and studios are participating in the project. Each short highlights a different hero from the game. The shorts are posted to #Compass\'s official YouTube channel.\r\n\r\n(Source: ANN) ', '2018', 'Summer');
