INSERT INTO `anime` VALUES (38700, 'Tsukiyo & Opal', 'https://cdn.myanimelist.net/images/anime/1849/96392.jpg', 'Movie', 'Original', 'Half asleep with my eyes closed, my conscience flies beyond time and space and I transform myself to all the life forms existing. I become the universe and the universe becomes me, until I fall asleep.... It is a song to pray for the existence of heart and soul at an awakening.', '2015', 'Winter');
