INSERT INTO `anime` VALUES (38938, 'Seishun Buta Yarou wa Bunny Girl Senpai no Yume wo Minai Picture Drama', 'https://cdn.myanimelist.net/images/anime/1315/99597.jpg', 'Special', 'Light novel', 'Picture drama episodes included on the DVD/Blu-ray Volumes of Seishun Buta Yarou wa Bunny Girl Senpai no Yume wo Minai.', '2018', 'Fall');
