INSERT INTO `anime` VALUES (39022, 'Ling Feng Zhe', 'https://cdn.myanimelist.net/images/anime/1851/98296.jpg', 'ONA', 'Original', 'The Leader will talk about how Marx met his wife Jenny and their thwarted love story, as well as his friendship with Friedrich Engels, his close companion and co-author of the Communist Manifesto.\r\n\r\n(Source: Global Times)', '2019', 'Winter');
