INSERT INTO `anime` VALUES (39274, 'Dragon Quest: Your Story', 'https://cdn.myanimelist.net/images/anime/1870/101614.jpg', 'Movie', 'Game', 'Following in his father\'s footsteps, Luca must find the hero who wields the zenithian sword to save his mother from Ladja. Based on Dragon Quest V: Hand of the Heavenly Bride game\'s story.\r\n\r\n(Source: Apple TV)', '2019', 'Summer');
