INSERT INTO `anime` VALUES (39477, 'Shingeki no Kyojin Season 3 Specials', 'https://cdn.myanimelist.net/images/anime/1909/99638.jpg', 'Special', 'Manga', 'Shingeki no Kyojin Season 3 DVD/BD specials. They are chibi parody shorts; one video was included on each of the DVD/BDs for a total of four. Each video contained three stories (essentially chibi parody short episode 38–49) which continued the chibi parody story from the previous chibi parody specials included on the DVD/BDs of the previous seasons.', '2018', 'Fall');
INSERT INTO `anime` VALUES (39478, 'Shingeki no Kyojin in the Dome: Heishi-tachi no Hoshizora', 'https://cdn.myanimelist.net/images/anime/1893/99639.jpg', 'Movie', 'Manga', 'A planetarium film screened originally at the Konica Minolta Planetarium \"Tenku\" in the Tokyo Sky Tree and then later on May 27th at the Konica Minolta Planetarium \"Manten\" in Sunshine City (a commercial building complex in  Ikebukuro).\r\n\r\nThe film is entirely CG and was done through the game engine Unity.', '2017', 'Spring');
INSERT INTO `anime` VALUES (39479, 'Tannishou wo Hiraku', 'https://cdn.myanimelist.net/images/anime/1053/99642.jpg', 'Movie', 'Book', 'Yuien, a troubled young man, meets the Buddhist monk Shinran, and listens to his teachings. Shinran takes him on as his disciple, and they travel together, with experiences both joyous and heartbreaking awaiting them on the road.\r\n\r\n(Source: ANN)', '2019', 'Spring');
