INSERT INTO `anime` VALUES (39918, 'Shining Star', 'https://cdn.myanimelist.net/images/anime/1712/101318.jpg', 'TV', 'Original', 'At the prestigious Shining Star School, every girl dreams of becoming the best idol, called \"Muse\". Nara, an ordinary girl, decides to attend the school in order to fulfill her dream.\r\n\r\n(Source: Shining Star Wiki)', '2018', 'Winter');
