INSERT INTO `anime` VALUES (39959, 'Ani ni Tsukeru Kusuri wa Nai! 3', 'https://cdn.myanimelist.net/images/anime/1756/103441.jpg', 'TV', 'Web manga', 'Shi Miao is still stuck with her irresponsible elder brother Shi Fen, who lives in neverending fear of his younger sister\'s stormy temper. As the siblings continue enjoying their carefree high school days alongside their friends and classmates, they realize that there truly is no cure for family.\r\n\r\n[Written by MAL Rewrite]', '2019', 'Fall');
