INSERT INTO `anime` VALUES (39978, 'Waresho! Warera! Shodobutsu Aigo Iinkai', 'https://cdn.myanimelist.net/images/anime/1901/101621.jpg', 'TV', 'Original', 'Three girls come from another planet to save the mistreated animals of planet Earth. Black cat Amiru, squirrel Rumi, and rabbit Rie all make their way to Earth but Rie crashes her UFO! Lucky Amiru and Rumi run into Earth girl Minami and together set out to conquer the world!\r\n\r\n(Source: Tokyo Otaku Mode)', '2019', 'Summer');
