INSERT INTO `anime` VALUES (40430, 'Uchuu Seibutsu Jirou', 'https://cdn.myanimelist.net/images/anime/1860/103195.jpg', 'ONA', 'Original', 'A mysterious cat from space called Uchuu Sebutsu Jirou has come to Earth. Jirou is often seen in places with music.\r\n\r\nJirou was created for promotional character goods purposes with toys, books, stickers, etc. This short promotional video will also be streamed at movie theaters starting on October 25, 2019.', '2019', 'Summer');
