INSERT INTO `anime` VALUES (40645, 'Kaigo to Ikiru', 'https://cdn.myanimelist.net/images/anime/1818/103921.jpg', 'ONA', 'Original', 'Japanese issuance company SOMPO Himawari Life Insurance commissioned Tekken to produce a commercial to promote their \"Insurhealth\" services, which seek to combine the functions of insurance and healthcare services. It follows a daughter caring for her aging mother that is facing various mental and physical complications after her husband passes away.', '2019', 'Summer');
