INSERT INTO `anime` VALUES (40823, 'Ni Zhuan Ci Yuan: AI Jue Qi', 'https://cdn.myanimelist.net/images/anime/1021/105729.jpg', 'ONA', 'Manga', 'Shi Jiajie and Xiao Lei, who used the game as a livelihood, accidentally received the entry of the \"Epoch\" game. In order to win a huge prize in the game, Shi Jiajie decided to enter the game. As everyone knows, but it is involved in the inexplicable pursuit, but this is only the prelude to this layer of conspiracy…\r\n\r\n(Source: Donghua Info)', '2019', 'Fall');
INSERT INTO `anime` VALUES (40832, 'Mukashi Keibanashi', 'https://cdn.myanimelist.net/images/anime/1430/104665.jpg', 'TV', 'Original', 'A fairytale and folklore parody anime focused on race horses. They take classic stories (like Momotarou or Cinderella) and put them in  race horse situations with the narrator voicing the action in typical fast-paced sports commentary. The anime is funded and promoted by the Japan Racing Association (JRA).', '2019', 'Fall');
INSERT INTO `anime` VALUES (40833, 'Inu to Neko Docchi mo Katteru to Mainichi Tanoshii', 'https://cdn.myanimelist.net/images/anime/1788/106525.jpg', 'TV', 'Manga', 'Matsumoto-san owns an innocent dog and an adorably devious cat. Laughing and crying ensue as they all live daily life together.\r\n\r\n(Source: MAL News)', '2020', 'Fall');
