INSERT INTO `anime` VALUES (40983, 'Hakushon Daimaou 2020', 'https://cdn.myanimelist.net/images/anime/1621/105178.jpg', 'TV', 'Original', '50 years have passed since the events of the original Hakushon Daimaou series. Akubi is training to become queen when she meets Kantarou Yodama, the grandson of the genie bottle\'s original owner, Kan-chan. Together, Akubi and Kantarou take on many jobs in the human world and learn the importance of having a dream.', '2020', 'Spring');
