INSERT INTO `anime` VALUES (41286, 'Da Wang Bu Gaoxing', 'https://cdn.myanimelist.net/images/anime/1003/106043.jpg', 'ONA', 'Web manga', 'We follow the daily life of Xiao Luo, a young yama (god and judge of the dead) who is afraid of ghosts. In order to properly reign in the underworld, she will have to face her fears.', '2020', 'Winter');
INSERT INTO `anime` VALUES (41293, 'Kumo no Mori no Marcus', 'https://cdn.myanimelist.net/images/anime/1937/106072.jpg', 'ONA', 'Picture book', 'I am Marcus, the number one pilot in the Cloud Forest!\r\n\r\nIn the clear blue sky full of white clouds, there exists a village called \"Cloud Forest\". This is the story about Marcus the pilot and a little boy named TT who live in the Cloud Forest.\r\n\r\n(Source: YouTube)', '2017', 'Fall');
