INSERT INTO `anime` VALUES (41353, 'The God of High School', 'https://cdn.myanimelist.net/images/anime/1722/107269.jpg', 'TV', 'Web manga', 'It all began as a fighting tournament to seek out for the best fighter among all high school students in Korea. Mori Jin, a Taekwondo specialist and a high school student, soon learns that there is something much greater beneath the stage of the tournament.\r\n\r\n(Source: LINE Webtoon)', '2020', 'Summer');
