INSERT INTO `anime` VALUES (41421, 'Kyouryuu Shoujo Gauko 2nd Season', 'https://cdn.myanimelist.net/images/anime/1284/106412.jpg', 'ONA', 'Original', 'The comedy series is set in Japan and follows Naoko Watanabe, a typical tween girl aside from the fact that she possesses strange and sometimes troubling powers. When her anger exceeds a maximum level, she turns into Gauko, the fire-breathing dinosaur girl.\r\n\r\n(Source: Variety)', '2020', 'Winter');
