INSERT INTO `anime` VALUES (41531, 'Hataraku Saibou Black', 'https://cdn.myanimelist.net/images/anime/1914/106749.jpg', 'ONA', 'Manga', 'A special animated commercial first released on Twitter to promote the manga. It was then released on Kodansha\'s official YouTube channel on January 15.\r\n\r\nSet inside an unhealthy body, Hataraku Saibou Black features the anthropomorphized cells of the human immune system as they attempt to keep their ailing world alive.', '2020', 'Winter');
INSERT INTO `anime` VALUES (41542, 'Windows Tablet no Uta', 'https://cdn.myanimelist.net/images/anime/1842/106761.jpg', 'ONA', 'Original', 'In 2014, Microsoft Japan commissioned Manabu Himeda to create a web commercial for their Windows 2 in 1 PCs. In the musically-driven performance, Himeda acts out choreographed movements in real time to interact with an animated video he created that is being projected on the wall behind him.', '2014', 'Fall');
INSERT INTO `anime` VALUES (41545, 'Ogaki Matsuri ni Ikou yo!', 'https://cdn.myanimelist.net/images/anime/1425/106763.jpg', 'ONA', 'Original', 'A tourism promotion anime for Ogaki City in the Gifu Prefecture. The story\'s script was written by Kou Nakamura, a famous novelist from Ogaki City.\r\n\r\nA love story set during the Ogaki Festival preparations. The festival itself is held in May.', '2020', 'Winter');
